import Input from '../../components/Ui/Backdrop/Input/Input';
import classes from '../Login/login.module.css';
import {checkValidity} from '../../shared/utility';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';
const RegisterAsSeller = props => {

    const[authForm,setAuthForm] =  useState({
            email:{
                elementType:'input',
                elementConfig:{
                    type:'email',
                    placeholder:'your email'
                },
                value:'',
                validation:{
                    required:true,
                    isEmail: true
                },
                valid : false,
                touched:false
            },
            firstName:{
                elementType:'input',
                elementConfig:{
                    type:'firstName',
                    placeholder:'your firstName'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            lastName:{
                elementType:'input',
                elementConfig:{
                    type:'lastName',
                    placeholder:'your lastName'
                },
                value:'',
                validation:{
                    required:true,
                },
                valid : false,
                touched:false
            },
            companyContactNo:{
                elementType:'input',
                elementConfig:{
                    type:'companyContactNo',
                    placeholder:'your companyContactNo'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            password:{
                elementType:'input',
                elementConfig:{
                    type:'password',
                    placeholder:'your password'
                },
                value:'',
                validation:{
                    required:true,
                    minLength: 8,
                    maxLength:15
                },
                valid : false,
                touched:false
            },
            confirmpassword:{
                elementType:'input',
                elementConfig:{
                    type:'password',
                    placeholder:'your confirmpassword'
                },
                value:'',
                validation:{
                    required:true,
                    minLength: 8,
                    maxLength:15
                },
                valid : false,
                touched:false
            },
            companyName:{
                elementType:'input',
                elementConfig:{
                    type:'companyName',
                    placeholder:'your companyName'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            city:{
                elementType:'input',
                elementConfig:{
                    type:'city',
                    placeholder:'your city'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            state:{
                elementType:'input',
                elementConfig:{
                    type:'state',
                    placeholder:'your state'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            country:{
                elementType:'input',
                elementConfig:{
                    type:'country',
                    placeholder:'your country'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            zipcode:{
                elementType:'input',
                elementConfig:{
                    type:'zipcode',
                    placeholder:'your zipcode'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            addressLine:{
                elementType:'input',
                elementConfig:{
                    type:'addressLine',
                    placeholder:'your addressLine'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            gstNo:{
                elementType:'input',
                elementConfig:{
                    type:'gstNo',
                    placeholder:'your gstNo'
                },
                value:'',
                validation:{
                    required:true,
                    isGst:true
                },
                valid : false,
                touched:false
            }
        })

        if(props.registerSuccessful)
        {
            props.history.push("/emailSent")
        }

        const registerHandler=(event)=>{
            event.preventDefault();
            props.onRegister(authForm);
        }

     const inputChangedHandler = (event,controlName)=>{
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value,authForm[controlName].validation),

                touched:true
            }
        };
        setAuthForm(updatedControls);
    }


        const formElementsArray=[];
        for(let key in authForm){
            formElementsArray.push({
                id:key,
                config:authForm[key]

            })
        }


        let form = formElementsArray.map(formElement=>(
            <>
            <label>{formElement.id}</label>
            <Input
            key={formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event) => inputChangedHandler(event, formElement.id)}
            invalid={!formElement.config.valid}
            shouldValidate={formElement.config.validation}
            touched={formElement.config.touched}
        />
        </>
        ))

        return (
            <div className={classes.Auth}>
                <form onSubmit={registerHandler}>
                 {form}
                 <button className={classes.Button}>Register</button>
                </form>
                {props.error}
            </div>
        )        
        }

const mapStateToProps = state=>{
    return{
        error:state.register.error,
        registerSuccessful:state.register.registerSuccessful        
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onRegister: (formData)=>dispatch(actions.registerAsSeller(formData))
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(RegisterAsSeller)
