import * as actionTypes from './actionTypes';
import axios from 'axios';
export const changePasswordSuccess = () => {
    return {
        type: actionTypes.CHANGEPASSWORD_SUCCESSS,
    }
}

export const changePasswordFail = (error) => {
    return {
        type: actionTypes.CHANGEPASSWORD_FAIL,
        error: error
    }
}

export const changePasswordStart=()=>{
    return{
        type: actionTypes.CHNAGEPASSWORD_START
    }
}

export const changePassword=(formData)=>{
        return dispatch => {
            dispatch(changePasswordStart())
            const data= {
                "password": formData.password.value,
                "confirmPassword": formData.confirmPassword.value,

         }
            const token = localStorage.getItem('token')
            const AuthStr = 'Bearer '.concat(token);
            axios.put("/editPassword/",data ,{ headers: { Authorization: AuthStr } })
                .then(response => {
                    dispatch(changePasswordSuccess())
                    window.location.reload(false)
                })
                .catch((error) => {
                    dispatch(changePasswordFail(error.response.data.message))
                });
        }
    }
