import classes from './singleProduct.module.css';
import {connect} from 'react-redux'

import One from '../../assets/images/1.jpg'
import Two from '../../assets/images/2.jpg'
import Three from '../../assets/images/manage-orders.jpg'

const SingleProduct=(props)=> {

  let brands=[]
  let sizes=[]
  let color=[]

    if(props.singleProductForCustomer!=null)
  {

    for(let i=0;i<props.singleProductForCustomer.fieldName.length;i++)
    {
      if(props.singleProductForCustomer.fieldName[i]==="SIZE")
      {
        console.log(props.singleProductForCustomer.values[i])
        var arr = props.singleProductForCustomer.values[i].split(',')
        console.log("arr is ",arr)
        for(let j=0;j<arr.length;j++)
         {
           sizes.push(
             <>
             <input type="checkbox" id={arr[j]} value=""/>
             <label for={arr[j]}>{arr[j]}</label>
             &nbsp;&nbsp;
             </>
             )
         }
