import React,{useEffect} from 'react'
import classes from '../Admin/AdminDashboard/adminDashboard.module.css'
import {connect} from 'react-redux'
import * as actions from '../../store/actions/index'

const SellerAccount=(props)=> {
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])

    const getCategoryHandler=()=>{
        props.history.push("/viewCategoriesForSeller")
    }

    return (
        <center>
            <h1>Your Account</h1>
        <div className={classes.Card} onClick={getCategoryHandler}> 
        <div className={classes.Container}>
             <h3><b>Get Category</b></h3>
            </div>
      </div>

      </center>
    )
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCategory()),
    }
}

export default connect(null,mapDispatchToProps)(SellerAccount)
