import React from 'react'
import classes from './product.module.css'
import One from '../../assets/images/14.jpg'
const ProductCards = (props) => {
     let content=null
     if(localStorage.getItem('roleName') === "ROLE_CUSTOMER")
     {
     if(props.isActive)
     {
        content=(<div className={classes.Card1} onClick={props.fetchSingleProductHandler}>
            <div>
                <img className={classes.Imgs} src={One} alt="MyPic" width="250" height="200" />
            </div>
            <div className={classes.Container}>
                <center><h3><b>{props.productname}</b></h3></center>
            </div>
        </div>
        )
     }   
    }
    else
    {
        if(localStorage.getItem('roleName') === "ROLE_ADMIN")
        {
            content=(<div className={classes.Card1} onClick={props.fetchSingleProductHandler}>
                <div>
                    <img className={classes.Imgs} src={One} alt="MyPic"  />
                </div>
                <div className={classes.Container}>
                    <center><h3><b>{props.productname}</b></h3></center>
                </div>
            </div>)
        }
        else
        {
            content=(<div className={classes.Card1} onClick={props.fetchSingleProductHandler}>
                <div>
                    <img className={classes.Imgs} src={One} alt="MyPic"  />
                </div>
                <div className={classes.Container}>
                    <center><h3><b>{props.productname}</b></h3></center>
                </div>
            </div>)
        }        
    }
    return (
        <>
        {content}
        </>
    )
}
export default ProductCards
