import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    customers:null
};

const getCustomersSuccess = (state,action)=>{
    return updateObject(state,{})

}

const saveCustomers=(state,action)=>{
    return updateObject(state,{
        customers:action.customers
    })

}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.GETCUSTOMERS_SUCCESS:
           return getCustomersSuccess(state,action)
        case actionTypes.SAVE_CUSTOMERS:
            return saveCustomers(state,action)
           default:
            return state;
        }

}

export default reducer;
