import * as actionTypes from './actionTypes';
import axios from 'axios';
export const getProductStart = () => {
    return {
        type: actionTypes.GETPRODUCT_START
    }
}
export const getProductSuccess = () => {
    return {
        type: actionTypes.GETPRODUCT_SUCCESS
    }
}

export const getProductFail = (error) => {
    return {
        type: actionTypes.GETPRODUCT_FAIL,
        error: error
    }
}

export const saveProducts = (products) => {
    return {
        type: actionTypes.SAVE_GETPRODUCT,
        products: products
    }
}
export const saveProductsForCustomer = (productsForCustomer) => {
    return {
        type: actionTypes.SAVE_PRODUCTS_FOR_CUSTOMER,
        productsForCustomer: productsForCustomer
    }
}
export const saveSingleProductForCustomer = (singleProductForCustomer) => {
    return {
        type: actionTypes.SAVE_SINGLE_PRODUCT_FOR_CUSTOMER,
        singleProductForCustomer: singleProductForCustomer
    }
}
export const getProducts = () => {
    return dispatch => {
        dispatch(getProductStart())
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.get("/getProductsForAdmin", { headers: { Authorization: AuthStr } })
            .then(response => {
                dispatch(saveProducts(response.data))
                dispatch(getProductSuccess())
            })
            .catch((error) => {
                dispatch(getProductFail())
                console.log('error ' + error);
            });
    }
}

export const getProductsForCustomer = (id) => {
    return dispatch => {
        dispatch(getProductStart())
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.get("/getProductsForCustomer/"+id, { headers: { Authorization: AuthStr } })
            .then(response => {
                dispatch(saveProductsForCustomer(response.data))
                dispatch(getProductSuccess())
            })
            .catch((error) => {
                dispatch(getProductFail())
                console.log('error ' + error);
            });
    }
}

export const getSingleProductForCustomer = (id) => {
    return dispatch => {
        dispatch(getProductStart())
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.get("/viewSingleProductForCustomer/"+id, { headers: { Authorization: AuthStr } })
            .then(response => {
                dispatch(saveSingleProductForCustomer(response.data))
                dispatch(getProductSuccess())
            })
            .catch((error) => {
                dispatch(getProductFail())
                console.log('error ' + error);
            });
    }
}

export const activateProduct = (id) => {
    return dispatch => {
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.put("/activateProduct/"+id, { headers: { Authorization: AuthStr } })
            .then(response => {
                window.location.reload(false)
            })
            .catch((error) => {
            });
    }
}

export const deactivateProduct = (id) => {
    return dispatch => {
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.put("/deactivateProduct/"+id, { headers: { Authorization: AuthStr } })
            .then(response => {
                window.location.reload(false)
            })
            .catch((error) => {
            });
    }
}
