import React,{useEffect} from 'react'
import {connect} from 'react-redux';
import * as actions from '../../../store/actions/index'
import ViewCategoriesAccordian from '../ViewCategoriesAccordian'
import classes from '../../ChangePassword/changePassword.module.css';
const ViewAllCategoriesForCustomer=(props)=> {

    // const {onClicked}=props
    // useEffect(()=>{
    //     onClicked()
    // },[onClicked])

    const subCategoriesHandler=(id)=>{
       props.onClicked1(id)
    }

    return (
        <div className={classes.Auth}>
        {props.customerCategories!==null?
        <>
        {                   
            props.customerCategories.map((customerCategories) => {
                console.log("object",customerCategories)
            return <ViewCategoriesAccordian
            name={customerCategories.name}
            key={customerCategories.id}
            clicked={subCategoriesHandler}
            />
        })}
        </>:null}

        </div>

    )
}
const mapStateToProps = state=>{
    return{
        customerCategories:state.category.customerCategories
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCategory()),
        onClicked1:(id)=>dispatch(actions.getSubCategoriesForCustomer(id))
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(ViewAllCategoriesForCustomer)
