import * as actionTypes from './actionTypes';
import axios from 'axios';
export const authStart = () => {
    return {
        type: actionTypes.AUTH_START
    }
}

export const authSuccess = (token) => {
    return {
        type: actionTypes.AUTH_SUCCESS,
        token: token
    }
}

export const authFail = (error) => {
    return {
        type: actionTypes.AUTH_FAIL,
        error: error
    }
}

export const logOut = () => {

    const token = localStorage.getItem('token')
    const AuthStr = 'Bearer '.concat(token);
    axios.get("/doLogout", { headers: { Authorization: AuthStr } })
        .then(response => {
        })
        .catch((error) => {
        });

    localStorage.removeItem('roleName');
    localStorage.removeItem('lastName');
    localStorage.removeItem('firstName');
    localStorage.removeItem('id');
    localStorage.removeItem('token');
    localStorage.removeItem('expirationDate');
    localStorage.removeItem('refreshToken');
    localStorage.removeItem('userName');
    return {
        type: actionTypes.AUTH_LOGOUT
    }

}
export const checkAuthTimeout = (expirationTime) => {
    return dispatch => {
        setTimeout(() => {
            dispatch(logOut());
        }, expirationTime * 1000);
    };
};

export const auth = (email, password) => {
    return dispatch => {
        dispatch(authStart());
        var bodyFormData = new FormData();
        bodyFormData.set("grant_type", "password");
        bodyFormData.append("client_id", "live-test");
        bodyFormData.append("username", email);
        bodyFormData.append("password", password);
        bodyFormData.append("client_secret", "abcde");
        axios.get("/getUser/" + email).then(response => {
            localStorage.setItem('id', response.data.split(" ")[0])
            localStorage.setItem('firstName', response.data.split(" ")[1])
            localStorage.setItem('lastName', response.data.split(" ")[2])
            localStorage.setItem('roleName', response.data.split(" ")[3])
            axios.post("/oauth/token", bodyFormData).then((response) => {
                const expirationDate = new Date(new Date().getTime() + response.data.expires_in * 1000);
                console.log('expiration date ',expirationDate)
                console.log('expires in ',response.data.expires_in)
                localStorage.setItem('token', response.data.access_token);
                localStorage.setItem('expirationDate', expirationDate);
                localStorage.setItem('refreshToken', response.data.refresh_token);
                localStorage.setItem('userName', email);
                dispatch(authSuccess(response.data.access_token));
                dispatch(checkAuthTimeout(response.data.expires_in))
            })
            return console.log(response.data);
        }).catch(error => {
            alert("username or password is wrong")
            dispatch(authFail(error.response.data.error))

        })
    }
}

export const setAuthRedirectPath = (path) => {
    return {
        type: actionTypes.SET_AUTH_REDIRECT_PATH,
        path: path
    }
}

export const authCheckState = () => {
    return dispatch => {
        const token = localStorage.getItem('token');
        if (!token) {
            dispatch(logOut());
        } else {
            const expirationDate = new Date(localStorage.getItem('expirationDate'));
            if (expirationDate <= new Date()) {
                dispatch(logOut());
            } else {
                dispatch(authSuccess(token));
                dispatch(checkAuthTimeout((expirationDate.getTime() - new Date().getTime()) / 1000));
            }
        }
    };
};
