import * as actionTypes from './actionTypes';
import axios from 'axios';
export const getAddressStart = () => {
    return {
        type: actionTypes.GETADDRESS_START
    }
}
export const getAddressSuccess = () => {
    return {
        type: actionTypes.GETADDRESS_SUCCESS
    }
}

export const getAddressFail = (error) => {
    return {
        type: actionTypes.GETADDRESS_FAIL,
        error: error
    }
}

export const saveAddress = (address) => {
    return {
        type: actionTypes.SAVE_ADDRESS,
        address: address
    }
}

export const getAddress = () => {
    return dispatch => {
        dispatch(getAddressStart())
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.get("/getAddresses", { headers: { Authorization: AuthStr } })
            .then(response => {
                dispatch(saveAddress(response.data))
                dispatch(getAddressSuccess())
            })
            .catch((error) => {
                dispatch(getAddressFail())
                console.log('error ' + error);
            });
    }
}

