import React,{useEffect} from 'react'
import classes from './adminDashboard.module.css'
import {connect} from 'react-redux'
import * as actions from '../../../store/actions/index';

const AdminDashboard=(props)=> {
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])

    const customersHandler=()=>{
        props.history.push("/getCustomers")
    }

    const sellersHandler=()=>{
        props.history.push("/getSellers")
    }

    const AddHandler=()=>{
        props.history.push("/addMetadataFields")
    }
    const AddMetadataFieldHandler=()=>{
        props.history.push("/addMetadatFieldName")
    }

    return (
        <center>
            <h1>Your Account</h1>
        <div className={classes.Card} onClick={customersHandler}> 
        <div className={classes.Container}>
             <h3><b>Get Customers</b></h3>
            </div>
      </div>
      <div className={classes.Card} onClick={sellersHandler}> 
            <div className={classes.Container}>
             <h3><b>Get Sellers</b></h3>
            </div>
      </div>
      <div className={classes.Card} onClick={AddHandler}> 
            <div className={classes.Container}>
             <h3><b>Add Metadata Fields</b></h3>
            </div>
      </div>
      <div className={classes.Card} onClick={AddMetadataFieldHandler}> 
            <div className={classes.Container}>
             <h3><b>Add Metadata Field Name</b></h3>
            </div>
      </div>

      </center>
    )
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCategory())
    }
}

export default connect(null,mapDispatchToProps)(AdminDashboard)
