import * as actionTypes from './actionTypes';
import axios from 'axios';
export const getCustomersSuccess = () => {
    return {
        type: actionTypes.GETCUSTOMERS_SUCCESS 
    }
}

export const saveCustomers=(customers)=>{
    return{
        type:actionTypes.SAVE_CUSTOMERS,
        customers:customers
    }
}

export const getCustomers=()=>{
    return dispatch=>{
        dispatch(getCustomersSuccess())   
    const token = localStorage.getItem('token')
    const AuthStr = 'Bearer '.concat(token); 
    axios.get("/listAllCustomers", { headers: { Authorization: AuthStr } })
     .then(response => {
         dispatch(saveCustomers(response.data))
         console.log(response.data[0]);
      })
     .catch((error) => {
         console.log('error ' + error);
      });      
}
}
