import React,{useEffect} from 'react'
import classes from './adminDashboard.module.css'
import {connect} from 'react-redux'
import * as actions from '../../../store/actions/index';

const AdminDashboard=(props)=> {
    const {onClicked,onClicked1,onClicked2,onClicked3}=props
    useEffect(()=>{
        onClicked()
        onClicked1()
        onClicked2()
        onClicked3()
    },[onClicked,onClicked1,onClicked2,onClicked3])

    const customersHandler=()=>{
        props.history.push("/getCustomers")
    }

    const sellersHandler=()=>{
        props.history.push("/getSellers")
    }

    const AddHandler=()=>{
        props.history.push("/addMetadataFields")
    }
    const AddMetadataFieldHandler=()=>{
        props.history.push("/addMetadatFieldName")
    }
    const ViewMetadataFieldHandler=()=>{
        props.history.push("/viewMetadataField")
    }

    const ViewAllCategoriesHandler=()=>{
        props.history.push("/viewAllCategoriesForAdmin")
    }
    const AddCategoryHandler=()=>{
        props.history.push("/addACategory")
    }
    const AddSubCategoryHandler=()=>{
        props.history.push("/addASubCategory")
    }

    return (
        <center>
            <h1>Your Account</h1>
        <div className={classes.Card} onClick={customersHandler}> 
        <div className={classes.Container}>
             <h3><b>Get Customers</b></h3>
            </div>
      </div>
      <div className={classes.Card} onClick={sellersHandler}> 
            <div className={classes.Container}>
             <h3><b>Get Sellers</b></h3>
            </div>
      </div>
      <div className={classes.Card} onClick={AddHandler}> 
            <div className={classes.Container}>
             <h3><b>Add Metadata Fields</b></h3>
            </div>
      </div>
      <div className={classes.Card} onClick={AddMetadataFieldHandler}> 
            <div className={classes.Container}>
             <h3><b>Add Metadata Field Name</b></h3>
            </div>
      </div>

      <div className={classes.Card} onClick={ViewMetadataFieldHandler}> 
            <div className={classes.Container}>
             <h3><b>View Metadata Field Name</b></h3>
            </div>
      </div>

      <div className={classes.Card} onClick={ViewAllCategoriesHandler}> 
            <div className={classes.Container}>
             <h3><b>View All Categories</b></h3>
            </div>
      </div>

      <div className={classes.Card} onClick={AddCategoryHandler}> 
            <div className={classes.Container}>
             <h3><b>Add a Root Category</b></h3>
            </div>
      </div>

      <div className={classes.Card} onClick={AddSubCategoryHandler}> 
            <div className={classes.Container}>
             <h3><b>Add a Sub Category</b></h3>
            </div>
      </div>

      </center>
    )
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCategory()),
        onClicked1:()=>dispatch(actions.viewCategoryMetadataField()),
        onClicked2:()=>dispatch(actions.getAllCategoriesForAdmin()),
        onClicked3:()=>dispatch(actions.getCategoriesForAdd())
    }
}

export default connect(null,mapDispatchToProps)(AdminDashboard)
