import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import {Provider} from 'react-redux';
import thunk from 'redux-thunk';
import {createStore, applyMiddleware, compose, combineReducers} from 'redux';
import authReducer from './store/reducers/auth';
import registerReducer from './store/reducers/register'
import forgotPasswordReducer from  './store/reducers/forgotPassword'
import getCustomersForAdminReducer from './store/reducers/getCustomersForAdmin'
import CustomerProfileReducer from './store/reducers/customerProfile';
import { BrowserRouter } from 'react-router-dom';
import CustomerAddress from './store/reducers/customerAddress'
import ChangePasswordReducer from './store/reducers/changePassword'
import SellerProfile from './store/reducers/sellerProfile'
import CategoryReducer from './store/reducers/category'
import ProductReducer from './store/reducers/product'
const composeEnhancers = process.env.NODE_ENV==='development' ? window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ :null || compose;
const rootReducer = combineReducers({
  auth:authReducer,
  register:registerReducer,
  forgotPassword:forgotPasswordReducer,
  admin:getCustomersForAdminReducer,
  customer:CustomerProfileReducer,
  address:CustomerAddress,
  changePassword:ChangePasswordReducer,
  seller:SellerProfile,
  category:CategoryReducer,
  product:ProductReducer

})
const store = createStore(rootReducer,composeEnhancers(
  applyMiddleware(thunk)
));
ReactDOM.render(
  <React.StrictMode>
    <Provider store={store}>
    <BrowserRouter>
    <App />
    </BrowserRouter>
    </Provider>
  </React.StrictMode>,
  document.getElementById('root')
);
// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
