import * as actionTypes from './actionTypes';
import axios from 'axios';
export const fetchCategoryStart = () => {
    return {
        type: actionTypes.FETCHCATEGORY_START
    }
}
export const fetchCategorySuccess = () => {
    return {
        type: actionTypes.FETCHCATEGORY_SUCCESS
    }
}

export const fetchCategoryFail = (error) => {
    return {
        type: actionTypes.FETCHCATEGORY_FAIL,
        error: error
    }
}

export const saveCategory = (category) => {
    return {
        type: actionTypes.SAVE_FETCHCATEGORY,
        category: category
    }
}

export const getCategory = () => {
    return dispatch => {
        dispatch(fetchCategoryStart())
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.get("/viewCategoriesForSeller", { headers: { Authorization: AuthStr } })
            .then(response => {
                dispatch(saveCategory(response.data))
                dispatch(fetchCategorySuccess())
            })
            .catch((error) => {
                dispatch(fetchCategoryFail())
                console.log('error ' + error);
            });
    }
}

