import React,{useState} from 'react'
import Input from '../../../components/Ui/Backdrop/Input/Input';
import classes from '../../ChangePassword/changePassword.module.css';
import { checkValidity, updateObject } from '../../../shared/utility';
import * as actions from '../../../store/actions/index';
import { connect } from 'react-redux';

const UpdateProduct=(props)=> {
    const[authForm,setAuthForm] =  useState({
        productName:{
            elementType:'input',
            elementConfig:{
                type:'productName',
                placeholder:'your productName'
            },
            value:props.singleproductForSeller.productname,
            validation:{
                required:true,
                minLength: 6
            },
            valid : false,
            touched:false
        },
        brand:{
            elementType:'input',
            elementConfig:{
                type:'brand',
                placeholder:'your brand'
            },
            value:props.singleproductForSeller.brand,
            validation:{
                required:true,
                minLength: 6
            },
            valid : false,
            touched:false
        },
        description:{
            elementType:'textarea',
            elementConfig:{
                type:'description',
                placeholder:'product description'
            },
            value:props.singleproductForSeller.description,
            validation:{
                required:true,
                minLength: 6
            },
            valid : false,
            touched:false
        },
        returnable: {
            elementType: 'select',
            elementConfig: {
              options: [
                { value: 'true', displayValue: 'true' },
                { value: 'false', displayValue: 'false' }
              ]
            },
            value: props.singleproductForSeller.returnable,
            validation: {},
            valid: true
          },
          cancellable: {
            elementType: 'select',
            elementConfig: {
              options: [
                { value: 'true', displayValue: 'true' },
                { value: 'false', displayValue: 'false' }
              ]
            },
            value: props.singleproductForSeller.cancellable,
            validation: {},
            valid: true
          }


    })

    if(props.addedSuccessfully)
    {
        props.history.push("/viewCategoriesForSeller")
    }

    const Handler=(event)=>{
        event.preventDefault();
        props.onFormSubmit(authForm,props.id);
    }

 const inputChangedHandler = (event,controlName)=>{
    const updatedControls = {
        ...authForm,
        [controlName]: {
            ...authForm[controlName],
            value: event.target.value,
            valid: checkValidity(event.target.value,authForm[controlName].validation),

            touched:true
        }
    };
    setAuthForm(updatedControls);
}

    const formElementsArray=[];
    for(let key in authForm){
        formElementsArray.push({
            id:key,
            config:authForm[key]

        })
    }


    let form = formElementsArray.map(formElement=>(
     <>
     <label>{formElement.id}</label>
        <Input

        key = {formElement.id}
        elementType={formElement.config.elementType}
        elementConfig={formElement.config.elementConfig}
        value={formElement.config.value}
        changed={(event)=>inputChangedHandler(event,formElement.id)}
        touched={formElement.config.touched}
        />
        </>
    ))

    return (
        <div className={classes.Auth}>
            <form onSubmit={Handler}>
             {form}
             <button className={classes.Button}>Submit</button>
            </form>
            <h2 style={{color:"red"}}>{props.error}</h2>

        </div>
    )

    }

    const mapStateToProps = state => {
        return {
            singleproductForSeller: state.product.singleproductForSeller
        }
    }
const mapDispatchToProps = dispatch => {
    return {
        onFormSubmit: (formData,id) => dispatch(actions.addProduct(formData,id))

    }
}
export default connect(mapStateToProps,mapDispatchToProps)(UpdateProduct)
