import * as actionTypes from './actionTypes';
import axios from 'axios';
export const getProductStart = () => {
    return {
        type: actionTypes.GETPRODUCT_START
    }
}
export const getProductSuccess = () => {
    return {
        type: actionTypes.GETPRODUCT_SUCCESS
    }
}

export const getProductFail = (error) => {
    return {
        type: actionTypes.GETPRODUCT_FAIL,
        error: error
    }
}

export const saveProducts = (products) => {
    return {
        type: actionTypes.SAVE_GETPRODUCT,
        products: products
    }
}

export const getProducts = () => {
    return dispatch => {
        dispatch(customerProfileStart())
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.get("/getProductsForAdmin", { headers: { Authorization: AuthStr } })
            .then(response => {
                dispatch(saveProducts(response.data))
                dispatch(getProductSuccess())
            })
            .catch((error) => {
                dispatch(getProductFail())
                console.log('error ' + error);
            });
    }
}
