import React from 'react'
import One from '../../assets/images/11.jpg'
import Two from '../../assets/images/22.jpg'
import Three from '../../assets/images/33.jpeg'
import { connect } from 'react-redux'
import './navbar.css';
import Product from '../Product/Product'
import * as actions from '../../store/actions/index'
import ViewAllCategoriesForCustomer from '../Category/VIewAllCategoriesForCustomer/ViewAllCategoriesForCustomer'
import CategorySpecificProduct from '../Product/CategorySpecificProduct'
const Navbar = (props) => {
  let brands = []
  let sizes = []
  let color = []
  if (props.filteringDetails != null) {
    let brandSet = [...new Set(props.filteringDetails.brands)]
    for (let i = 0; i < brandSet.length; i++) {
      brands.push(
        <React.Fragment key={brandSet[i]}>
        <div style={{ marginLeft: "30px" }} className="checkbox">
          <input type="checkbox" id={brandSet[i]} value="" />
          <label htmlFor={brandSet[i]}>{brandSet[i]}</label>
        </div>
        </React.Fragment>
      )
    }
    for (let i = 0; i < props.filteringDetails.fields.length; i++) {
      if (props.filteringDetails.fields[i] === "SIZE") {
        let arr = props.filteringDetails.values[i].split(',')
        for (let j = 0; j < arr.length; j++) {
          sizes.push(
            <React.Fragment key={arr[j]}>
              <div style={{ marginLeft: "30px" }} className="checkbox">
                <input type="checkbox" id={arr[j]} value="" />
                <label htmlFor={arr[j]}>{arr[j]}</label>
              </div>
            </React.Fragment>
          )
        }
      }
      if (props.filteringDetails.fields[i] === "COLOR") {
        let arr = props.filteringDetails.values[i].split(',')
        for (let j = 0; j < arr.length; j++) {
          color.push(
            <React.Fragment key={arr[j]}>
            <div style={{ marginLeft: "30px" }} className="checkbox">
              <input type="checkbox" id={arr[j]} value="" />
              <label htmlFor={arr[j]}>{arr[j]}</label>
            </div>
            </React.Fragment>
          )
        }
      }
    }
  }
  return (
    <div style={{ height: "700px", boxShadow: "10px" }}>
      <div style={{ backgroundColor: "white", float: "left", boxShadow: "20px", borderColor: "black" }} className="navbar navbar-inverse navbar-fixed-left">
        <p>Shop By Category</p>
        <ViewAllCategoriesForCustomer />
        <hr />
        {props.filteringDetails !== null ?
          <>
            <h5>SIZE</h5>
            {sizes}
            <hr />
            <h5>Color</h5>
            {color}
            <hr />
            <h5>BRANDS</h5>
            {brands}
          </> : null}
      </div>
      <div className="Corousel">
        <div className="container" style={{ width: "90%", marginLeft: "125px", height: "300px" }}>
          <div id="myCarousel" className="carousel slide" data-ride="carousel">
            <ol className="carousel-indicators">
              <li data-target="#myCarousel" data-slide-to="0" className="active"></li>
              <li data-target="#myCarousel" data-slide-to="1"></li>
              <li data-target="#myCarousel" data-slide-to="2"></li>
            </ol>
            <div className="carousel-inner" style={{ height: "300px" }}>
              <div className="item active">
                <img src={One} alt="Los Angeles" width="100%" />
              </div>

              <div className="item">
                <img src={Two} alt="Chicago" width="100%" />
              </div>

              <div className="item">
                <img src={Three} alt="New york" width="100%" />
              </div>
            </div>
            <a className="left carousel-control" href="#myCarousel" data-slide="prev">
              <span className="glyphicon glyphicon-chevron-left"></span>
              <span className="sr-only">Previous</span>
            </a>
            <a className="right carousel-control" href="#myCarousel" data-slide="next">
              <span className="glyphicon glyphicon-chevron-right"></span>
              <span className="sr-only">Next</span>
            </a>
          </div>
        </div>
      </div>
      <div className="Cards">
        {props.flag === true ? <CategorySpecificProduct /> : <Product />}
      </div>
    </div>
  )
}
const mapStateToProps = state => {
  return {
    error: state.product.error,
    products: state.product.products,
    filteringDetails: state.category.filteringDetails,
    id: state.category.id,
    productsForCustomer: state.product.productsForCustomer,
    flag: state.category.flag
  }
}
const mapDispatchToProps = dispatch => {
  return {
    productOfCategory: (id) => dispatch(actions.getProductsForCustomer(id))
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Navbar)
