import React from 'react'
import One from '../../assets/images/1.jpg'
import Two from '../../assets/images/2.jpg'
import Three from '../../assets/images/manage-orders.jpg'
import { connect } from 'react-redux'
import './navbar.css';
import Product from '../Product/Product'
import * as actions from '../../store/actions/index'
import ViewAllCategoriesForCustomer from '../Category/VIewAllCategoriesForCustomer/ViewAllCategoriesForCustomer'
import CategorySpecificProduct from '../Product/CategorySpecificProduct'
const Navbar = (props) => {

let brands=[]
let sizes=[]
let color=[]

  if(props.filteringDetails!=null)
{

let brandSet = [...new Set(props.filteringDetails.brands)]

console.log("brandset is",brandSet)
  for(let i=0;i<brandSet.length;i++)
  {
    console.log(brandSet[i])
     brands.push(
     <div style={{marginLeft:"30px"}} class="checkbox">
     <input type="checkbox" id={brandSet[i]} value=""/>
     <label for={brandSet[i]}>{brandSet[i]}</label>
     </div>
     )
  }

  for(let i=0;i<props.filteringDetails.fields.length;i++)
  {
    if(props.filteringDetails.fields[i]==="SIZE")
    {
      console.log(props.filteringDetails.values[i])
      var arr = props.filteringDetails.values[i].split(',')
      console.log("arr is ",arr)
      for(let j=0;j<arr.length;j++)
       {
         sizes.push(
           <div style={{marginLeft:"30px"}} class="checkbox">
           <input type="checkbox" id={arr[j]} value=""/>
           <label for={arr[j]}>{arr[j]}</label>
           </div>
           )
       }
      // let i = props.filteringDetails.values[i]
      // var arr = i.split(',')
      // for(let j=0;j<arr.length;j++)
      // {
      //   sizes.push(
      //     <div style={{marginLeft:"30px"}} class="checkbox">
      //     <input type="checkbox" id={arr[i]} value=""/>
      //     <label for={arr[i]}>{arr[i]}</label>
      //     </div>
      //     )
      // }
    }
    if(props.filteringDetails.fields[i]==="COLOR")
    {
      console.log(props.filteringDetails.values[i])
      var arr = props.filteringDetails.values[i].split(',')
      console.log("arr is ",arr)
      for(let j=0;j<arr.length;j++)
       {
         color.push(
           <div style={{marginLeft:"30px"}} class="checkbox">
           <input type="checkbox" id={arr[j]} value=""/>
           <label for={arr[j]}>{arr[j]}</label>
           </div>
           )
       }
      // let i = props.filteringDetails.values[i]
      // var arr = i.split(',')
      // for(let j=0;j<arr.length;j++)
      // {
      //   sizes.push(
      //     <div style={{marginLeft:"30px"}} class="checkbox">
      //     <input type="checkbox" id={arr[i]} value=""/>
      //     <label for={arr[i]}>{arr[i]}</label>
      //     </div>
      //     )
      // }
    }

  }
}

return (
    <div style={{ height: "700px", boxShadow: "10px" }}>
      <div style={{ backgroundColor: "white", float: "left", boxShadow: "20px", borderColor: "black" }} class="navbar navbar-inverse navbar-fixed-left">
        <p>Shop By Category</p>
        <ViewAllCategoriesForCustomer />
        <hr />
        {props.filteringDetails !== null ?
          <p>
            <h5>SIZE</h5>
            {sizes}
            <hr/>
            <h5>Color</h5>
            {color}
            <hr/>
            <h5>BRANDS</h5>
            {brands}
          </p> : null}
      </div>
      <div style={{ marginLeft: "200px", height: "300px" }}>
        <div class="container" style={{ width: "90%", marginLeft: "125px", height: "300px" }}>
          <div id="myCarousel" class="carousel slide" data-ride="carousel">
            <ol class="carousel-indicators">
              <li data-target="#myCarousel" data-slide-to="0" class="active"></li>
              <li data-target="#myCarousel" data-slide-to="1"></li>
              <li data-target="#myCarousel" data-slide-to="2"></li>
            </ol>
            <div class="carousel-inner" style={{ height: "300px" }}>
              <div class="item active">
                <img src={One} alt="Los Angeles" width="100%" />
              </div>

              <div class="item">
                <img src={Two} alt="Chicago" width="100%" />
              </div>

              <div class="item">
                <img src={Three} alt="New york" width="100%" />
              </div>
            </div>

            <a class="left carousel-control" href="#myCarousel" data-slide="prev">
              <span class="glyphicon glyphicon-chevron-left"></span>
              <span class="sr-only">Previous</span>
            </a>
            <a class="right carousel-control" href="#myCarousel" data-slide="next">
              <span class="glyphicon glyphicon-chevron-right"></span>
              <span class="sr-only">Next</span>
            </a>
          </div>
        </div>
      </div>
      <div style={{ marginLeft: "325px" }}>
        {props.id!=null&&props.flag===true?<CategorySpecificProduct/>:<Product />}

      </div>
    </div>
  )
}
const mapStateToProps = state => {
  console.log(state.category.flag, "value of flag    ")

  return {
    error: state.product.error,
    products: state.product.products,
    filteringDetails: state.category.filteringDetails,
    id:state.category.id,
    productsForCustomer:state.product.productsForCustomer,
    flag:state.category.flag
  }
}
const mapDispatchToProps = dispatch =>{
  return{
      productOfCategory: (id)=>dispatch(actions.getProductsForCustomer(id))
      }
}
export default connect(mapStateToProps,mapDispatchToProps)(Navbar)
