import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    error:null,
    products:null,
    updatedSuccessfully:false,
    productsForCustomer:null
};

const getProductStart = (state,action)=>{
    return updateObject(state,{error:null})

}
const getProductSuccess = (state,action)=>{
    return updateObject(state,{
        error:null,
    updatedSuccessfully:true})

}

const getProductFail=(state,action)=>{
    return updateObject(state,{
        error:action.error
    })

}

const saveProducts=(state,action)=>{
    return updateObject(state,{
        products:action.products
    })

}

const saveProductsForCustomer=(state,action)=>{
    return updateObject(state,{
        productsForCustomer:action.productsForCustomer
    })

}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.GETPRODUCT_START:
           return getProductStart(state,action)
        case actionTypes.GETPRODUCT_SUCCESS:
            return getProductSuccess(state,action)
            case actionTypes.GETPRODUCT_FAIL:
                return getProductFail(state,action)
                case actionTypes.SAVE_GETPRODUCT:
                    return saveProducts(state,action)
           default:
            return state;
        }

}

export default reducer;
