import React,{useEffect} from 'react'
import { connect } from 'react-redux'
import {withRouter} from 'react-router-dom'
import ProductCards from './ProductCards';
import * as actions from '../../store/actions/index'
const CategorySpecificProduct = (props) => {
    useEffect(() => {
    },[props.productsForCustomer])

    const fetchSingleProductHandle=(id)=>{
        props.fetchSingleProduct(id)
      props.history.push("/singleProduct")
      }
    return (
        <>
            {props.productsForCustomer != null ?

                <>
                    {
                        props.productsForCustomer.map((product) => {
                            return <ProductCards
                            isActive={product.active}
                                productname={product.productname}
                                description={product.description}
                                key={product.id}
                                fetchSingleProductHandler={()=>fetchSingleProductHandle(product.id)}
                            />
                        })}
               </>  : null}
        </>

    )
}
const mapStateToProps = state => {
    return {
        error: state.product.error,
        productsForCustomer: state.product.productsForCustomer
    }
}
const mapDispatchToProps = dispatch =>{
    return{
        fetchSingleProduct: (id)=>dispatch(actions.getSingleProductForCustomer(id))
        }
  }


export default withRouter(connect(mapStateToProps,mapDispatchToProps)(CategorySpecificProduct))
