import React from 'react'
import {connect} from 'react-redux'
import { Col } from 'react-bootstrap';
import {Panel} from 'react-bootstrap'
import  classes from './product.module.css'
import {withRouter} from 'react-router-dom'
import ProductCards from './ProductCards';
import * as actions from '../../store/actions/index'
const Product=(props)=> {

    // const {fetchSingleProduct}=props
    // useEffect(()=>{
    //     fetchSingleProduct(id)
    // },[fetchSingleProduct])

    console.log(props.history,"props history")
    const fetchSingleProduct=(id)=>{
      props.fetchSingleProduct(id)
      props.history.push("/singleProduct")
    }
    return (
        <>
        {props.products!=null?
        <>
        {             
            props.products.map((product) => {
            return <ProductCards
            isActive={product.active}
            productname={product.productname}
            description={product.description}
            fetchSingleProductHandler={()=>fetchSingleProductHandle(product.id)}
            />
        })}
        </>:null}
        </>

    )
       }
const mapStateToProps = state=>{
    console.log(state.product.products,"products    in Product")
    return{
        error:state.product.error,
        products:state.product.products
    }
}
const mapDispatchToProps = dispatch =>{
    return{
        fetchSingleProduct: (id)=>dispatch(actions.getSingleProductForCustomer(id))
        }
  }

export default withRouter(connect(mapStateToProps,mapDispatchToProps)(Product))
