import React,{useEffect} from 'react'
import  CustomerAddressCards from './customerAddressCards'
import {connect} from 'react-redux';
import * as actions from '../../../store/actions/index'
import classes from './customerAddressCards.module.css';
const CustomerAddress=(props)=> {

    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])

    const AddNew=()=>{
        props.history.push("/addNewAddress")
    }
    const updateNewAddress=()=>{
        props.history.push("updateAddress")
    }

    return (
        <React.Fragment>
        {props.address!==null?
        <>
        {                   
            props.address.map((address) => {
            return <CustomerAddressCards
            city={address.city}
            state={address.state}
            country={address.country}
            zipcode={address.zipcode}
            addressLine={address.addressLine}
            deleteHandler={()=>props.onDelete(address.id)}
            addNewAddress={AddNew}/>
        })}
        </>:<div className={classes.Card} > 
            <div className={classes.Container}>
            <h2>Add</h2>
            </div>
           </div>}



        </React.Fragment>

    )
}
const mapStateToProps = state=>{
    console.log(state.address.address)
    return{
        address:state.address.address
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getAddress()),
        onDelete: (id)=>dispatch(actions.deleteAddress(id)),
        onAddNewAddress:()=>dispatch(actions.addNewAddress())
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(CustomerAddress)
