import React, { useState,useEffect } from 'react'
import Input from '../Ui/Backdrop/Input/Input';
import classes from '../Login/login.module.css';
import {checkValidity,updateObject} from '../../shared/utility';
import { connect } from 'react-redux';
import * as actions from '../../store/actions/index';
const UpdateProfile = props => {

    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])


    const[authForm,setAuthForm] =  useState({
            firstName:{
                elementType:'input',
                elementConfig:{
                    type:'firstName',
                    placeholder:'your email'
                },
                value:props.profile.firstName,
                validation:{
                    required:true,
                    isEmail: true
                },
                valid : false,
                touched:false
            },
            lastname:{
                elementType:'input',
                elementConfig:{
                    type:'lastName',
                    placeholder:'your password'
                },
                value:props.profile.lastName,
                validation:{
                    required:true,
                    minLength: 6
                },
                valid : false,
                touched:false
            }
        })

        const submitHandler=(event)=>{
            event.preventDefault();
            props.onAuth(authForm.firstName.value)
        }

     const inputChangedHandler = (event,controlName)=>{
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value,authForm[controlName].validation),

                touched:true
            }
        };
        setAuthForm(updatedControls);
    }


        const formElementsArray=[];
        for(let key in authForm){
            formElementsArray.push({
                id:key,
                config:authForm[key]

            })
        }


        let form = formElementsArray.map(formElement=>(
            <Input
            key = {formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event)=>inputChangedHandler(event,formElement.id)}
            touched={formElement.config.touched}
            />
        ))

        return (
            <React.Fragment>
            <div className={classes.Auth}>

                <form onSubmit={submitHandler}>
                 {form}
                 <button className={classes.Button}>Submit</button>
                </form>

            </div>
             </React.Fragment>
        )

        }
        const mapStateToProps = state=>{
            return{
                profile:state.customer.profile
            }
        }

        const mapDispatchToProps = dispatch =>{
            return{
                onClicked: ()=>dispatch(actions.getProfile()),
                onAuth:(formData)=>dispatch(actions.updateProfile(formData))
                }
        }


export default connect(mapStateToProps,mapDispatchToProps)(UpdateProfile)
