import React, { useState,useEffect } from 'react'
import Input from '../../components/Ui/Backdrop/Input/Input';
import classes from './login.module.css';
import {checkValidity,updateObject} from '../../shared/utility';
import { connect } from 'react-redux';
import * as actions from '../../store/actions/index'

const Login = props => {

    const[authForm,setAuthForm] =  useState({
            email:{
                elementType:'input',
                elementConfig:{
                    type:'email',
                    placeholder:'your email'
                },
                value:'',
                validation:{
                    required:true,
                    isEmail: true
                },
                valid : false,
                touched:false
            },
            password:{
                elementType:'input',
                elementConfig:{
                    type:'password',
                    placeholder:'your password'
                },
                value:'',
                validation:{
                    required:true,
                    minLength: 6
                },
                valid : false,
                touched:false
            }
        })
        const {authRedirectPath,onSetAuthRedirectPath} = props;
        useEffect(()=>{
            console.log(authRedirectPath)
            if(authRedirectPath!=='/'){
                onSetAuthRedirectPath();
            }
        },[authRedirectPath,onSetAuthRedirectPath])

     const inputChangedHandler = (event,controlName)=>{
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value,authForm[controlName].validation),

                touched:true
            }
        };
        setAuthForm(updatedControls);
    }

    const submitHandler=(event)=>{
        event.preventDefault();
        props.onAuth(authForm.email.value,authForm.password.value)
    }

    const redirectToSignup=()=>{
       props.history.push("/register")
    }

        const formElementsArray=[];
        for(let key in authForm){
            formElementsArray.push({
                id:key,
                config:authForm[key]

            })
        }


        let form = formElementsArray.map(formElement=>(
            <Input
            key = {formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event)=>inputChangedHandler(event,formElement.id)}
            touched={formElement.config.touched}
            />
        ))

        return (
            <div className={classes.Auth}>

                <form onSubmit={submitHandler}>
                 {form}
                 <button>Login</button>
                 <br/>
                 <label>New to this site</label>
                 <br/>
                 <button onClick={redirectToSignup}>SignUp now</button>
                </form>
            </div>
        )

        }

const mapStateToProps = state=>{
            return{
                isAuthenticated:state.token!==null,
                authRedirectPath:state.authRedirectPath
            }
        }

const mapDispatchToProps = dispatch =>{
            return{
                onAuth: (email,passsword)=>dispatch(actions.auth(email,passsword)),
                // onSetAuthRedirectPath: ()=> dispatch(actions.setAuthRedirectPath('/'))
            }
        }


export default connect(mapStateToProps,mapDispatchToProps)(Login)
