            validation: {},
            valid: true
        },
        metadataFields: {
            elementType: 'select',
            elementConfig: {
                options: metaid
            },
            value: option1[0].value,
            validation: {},
            valid: true
        },
        categoryMetadataField: {
            elementType: 'input',
            elementConfig: {
                type: 'categoryMetadataField',
                placeholder: 'your categoryMetadataFieldValues'
            },
            value: '',
            validation: {
                required: true,
                minLength: 6
            },
            valid: false,
            touched: false
        }
    })
    // const a=(id)=>{
