    return {
        onClicked: () => dispatch(actions.getCategory()),
        onClicked1:()=>dispatch(actions.viewCategoryMetadataField),
        onAdd:(id1,id2,values)=>dispatch(actions.add(id1,id2,values))


    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UpdateMetadataValues)
