import React,{useEffect} from 'react';
import axios from 'axios';
import CustomerTable from './CustomerTable/CustomerTable'
import * as actions from '../../../store/actions/index'
import {connect} from 'react-redux'
const AdminGetCustomers=(props)=> {
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])

    const activateHandler=(id)=>{
        props.onActivate(id)
    }
    const deactivateHandler=(id)=>{
        props.onDeActivate(id)
    }
    return (
        <React.Fragment>
        {props.customers!==null?
        <div>
        {                   
            props.customers.map((customer,id) => {
            return <CustomerTable
            key={customer.id}
            isCustomer={true}
            firstName={customer.firstName}
            middleName={customer.middleName}
            id={customer.id}
            lastName={customer.lastName}
            username={customer.username}
            active={customer.active}
            activate={()=>activateHandler(customer.id)}
            deactivate={()=>deactivateHandler(customer.id)}
            />
        })}
        </div>:null}    

        </React.Fragment>
    )
}
const mapStateToProps = state=>{
    console.log(state.admin.customers)
    return{

        customers:state.admin.customers
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCustomers()),
        onActivate:(id)=>dispatch(actions.activateCustomer(id)),
        onDeActivate:(id)=>dispatch(actions.deactivateCustomer(id))
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(AdminGetCustomers)
