import React from 'react'
import classes from './product.module.css'
const ProductCards = (props) => {
    let content=null
    if(localStorage.getItem('roleName') === "ROLE_CUSTOMER")
    {
     if(props.isActive)
     {
        content=(<div className={classes.Card1} onClick={props.fetchSingleProductHandler}>
            <div>
                <img className={classes.Imgs} src="https://dummyimage.com/600x400/55595c/fff" alt="MyPic" width="250" height="200" />
            </div>
            <div className={classes.Container}>
                <center><h3><b>{props.productname}</b></h3></center>
            </div>
        </div>)
     }   
    }
    else
    {
        content=(<div className={classes.Card1} onClick={props.fetchSingleProductHandler}>
            <div>
                <img className={classes.Imgs} src="https://dummyimage.com/600x400/55595c/fff" alt="MyPic" width="250" height="200" />
            </div>
            <div className={classes.Container}>
                <center><h3><b>{props.productname}</b></h3></center>
            </div>
        </div>)


    }
    return (
        {content}
    )
}

export default ProductCards
